import React, { Component } from 'react';
import Chart from 'chart.js';
// import Chart from "chart.min.js";
import './App.scss';
export default class DonutChart extends Component {
  refDonut = React.createRef();

  componentDidMount() {
    const donutChartRef = this.refDonut.current.getContext('2d');

    new Chart(donutChartRef, {
      type: 'doughnut',
      data: {
        //Bring in data
        labels: ['Individuals', 'Companies'],

        datasets: [
          {
            label: 'Total Signups',
            backgroundColor: ['61005B','#61dafb'],

            data: [284, 364],
          },
        ],
      },
      options: {
        //Customize chart options
        cutoutPercentage: 85,
      },
    });
  }
  render() {
    return (
      <div className='signups-container'>
        <canvas id='myChart' width='50' height='100' ref={this.refDonut} />
      </div>
    );
  }
}
